// page init
jQuery(function(){
	initSlideShow();
	initAnchors();
	initInput();
	initMobileNav();
	initFitVids();
	initPopups();
});

// fade gallery init
function initSlideShow() {
	jQuery('.slideshow').fadeGallery({
		slides: '.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		event: 'click',
		useSwipe: true,
		autoRotation: true,
		autoHeight: true,
		switchTime: 3000,
		animSpeed: 500
	});
}

// mobile menu init
function initMobileNav() {
	jQuery('body').mobileNav({
		hideOnClickOutside: true,
		menuActiveClass: 'nav-active',
		menuOpener: '.nav-opener',
		menuDrop: '.menu'
	});
}

// initialize smooth anchor links
function initAnchors() {
	new SmoothScroll({
		anchorLinks: 'a.anchor[href^="#"]:not([href="#"])'
	});
}

// handle flexible video size
function initFitVids() {
	jQuery('#main').fitVids();
}

function initInput() {
	var defaultVal = '';
	$('.input-holder input, .input-holder textarea').focus(function(event) {
		$(this).closest('.input-holder').addClass('focus');
	}).blur(function(event) {
		$(this).closest('.input-holder').removeClass('focus');
		var actionVal = $(this).val();
		if (actionVal != defaultVal) $(this).closest('.input-holder').addClass('enter');
		else $(this).closest('.input-holder').removeClass('enter');
	}).keyup(function(event) {
		var actionVal = $(this).val();
		if (actionVal != defaultVal) $(this).closest('.input-holder').addClass('enter');
		else $(this).closest('.input-holder').removeClass('enter');
	}).change(function(event) {
		var actionVal = $(this).val();
		if (actionVal != defaultVal) $(this).closest('.input-holder').addClass('enter');
		else $(this).closest('.input-holder').removeClass('enter');
		$(this).blur();
	});
	$('.input-holder input, .input-holder textarea').each(function(index, el) {
		if($(this).val() != '') $(this).closest('.input-holder').addClass('enter');
		if($(this).is(':disabled')) $(this).closest('.input-holder').addClass('disable');
	});
}

// popups init
function initPopups() {
	jQuery('#nav > li').contentPopup({
		mode: 'hover',
		popup: '.sub-menu',
		btnOpen: '> a',
		openClass: 'hover'
	});
}

